mapper.insert:
INSERT
  INTO
       NOTICE
       (
       NOTICE_NO
       ,CATEGORY
       ,TITLE
       ,CONTENT
       ,MEM_NO
       ,CREATE_DATE
       ,ORIGIN_NAME
       ,CHANGE_NAME
       )
VALUES
       (
       SEQ_NNO.NEXTVAL
       ,#{category}
       ,#{title}
       ,#{content}
       ,#{memNo}
       ,SYSDATE
       ,#{originName}
       ,#{changeName}
       )

mapper.update:
	UPDATE
	       NOTICE
	   SET
		   CATEGORY = #{category}
		   ,TITLE = #{title}
		   ,CONTENT = #{content}
		   ,ORIGIN_NAME = #{originName}
		   ,CHANGE_NAME = #{changeName}
     WHERE
           NOTICE_NO = #{noticeNo}
       AND
           STATUS = 'Y'
