mapper.select:
SELECT BATTLE_NO ,KOR_AREA AS AREA ,CASE WHEN GENDER = 'A' THEN '남녀모두' WHEN GENDER = 'F' THEN '여자만' ELSE '남자만' END "GENDER" ,HOME_TEAM ,CASE WHEN "LEVEL" = 'A' THEN '무관' WHEN "LEVEL" = 'B' THEN '초보' WHEN "LEVEL" = 'M' THEN '중수' WHEN "LEVEL" = 'S' THEN '고수' END "LEVEL" ,BATTLE_DATE ,CHANGE_NAME ,CASE WHEN "STYLE" = 'f4' THEN '4:4 자유형' WHEN "STYLE" = 'h4' THEN '4:4 혼영' WHEN "STYLE" = 'hg4' THEN '4:4 혼계영' END "STYLE" ,TITLE ,BATTLE_TIME
FROM BATTLE JOIN AREA ON (AREA = ENG_AREA) WHERE BATTLE_DATE = #{cpage}
AND AREA = #{area}
AND GENDER = #{gender}
AND "LEVEL" = #{level}
AND STYLE = #{style}
ORDER BY BATTLE_TIME

mapper.insert:
INSERT
  INTO 
        BATTLE
        (
        BATTLE_NO
        ,AREA
        ,GENDER
        ,HOME_TEAM
        ,"LEVEL"
        ,BATTLE_DATE
        ,ORIGIN_NAME
        ,CHANGE_NAME
        ,STYLE
        ,TITLE
        ,BATTLE_TIME
        ,BATTLE_RULE
        )
VALUES
        (
        SEQ_BTNO.NEXTVAL
        ,#{area}
        ,#{gender}
        ,#{homeTeam}
        ,#{level}
        ,#{battleDate}
        ,#{originName}
        ,#{changeName}
        ,#{style}
        ,#{title}
        ,#{battleTime}
        ,#{battleRule}
        )

INSERT 
  INTO
        POOL_INFO
        (
        BATTLE_NO
        ,POOL_NAME
        ,LENGTH
        ,WIDTH
        ,LANES
        ,DEPTH
        ,PLACE
        ,YES_NO
        )
VALUES
        (
        SEQ_BTNO.CURRVAL
        ,#{poolName}
        ,#{length}
        ,#{width}
        ,#{lanes}
        ,#{depth}
        ,#{place}
        ,#{yesNo}
        )

mapper.update:
UPDATE
        BATTLE
   SET
        AWAY_TEAM = #{teamNo}
 WHERE
        BATTLE_NO = #{battleNo}

mapper.insert:
INSERT
  INTO
        CHATTING
        (
        CHAT_NO
        ,MOIM_NO
        ,MEM_NO
        ,CHAT_CONTENT
        )
VALUES
        (
        SEQ_CNO.NEXTVAL
        ,#{battleNo}
        ,#{memNo}
        ,#{chatContent}
        )

mapper.update:
UPDATE
        BATTLE_RESULT
   SET
        VICTORY = #{victory}
        ,DEFEAT = #{defeat}
        ,VIC_RECORD = #{vicRecord}
        ,DEF_RECORD = #{defRecord}
 WHERE
        BATTLE_NO = #{battleNo}
