mapper.insert:
INSERT
  INTO 
        BATTLE
        (
        BATTLE_NO
        ,AREA
        ,GENDER
        ,HOME_TEAM
        ,"LEVEL"
        ,BATTLE_DATE
        ,ORIGIN_NAME
        ,CHANGE_NAME
        ,STYLE
        ,TITLE
        ,BATTLE_TIME
        ,BATTLE_RULE
        )
VALUES
        (
        SEQ_BTNO.NEXTVAL
        ,#{area}
        ,#{gender}
        ,#{homeTeam}
        ,#{level}
        ,#{battleDate}
        ,#{originName}
        ,#{changeName}
        ,#{style}
        ,#{title}
        ,#{battleTime}
        ,#{battleRule}
        )

INSERT 
  INTO
        POOL_INFO
        (
        BATTLE_NO
        ,POOL_NAME
        ,LENGTH
        ,WIDTH
        ,LANES
        ,DEPTH
        ,PLACE
        ,YES_NO
        )
VALUES
        (
        SEQ_BTNO.CURRVAL
        ,#{poolName}
        ,#{length}
        ,#{width}
        ,#{lanes}
        ,#{depth}
        ,#{place}
        ,#{yesNo}
        )

mapper.update:
UPDATE
        BATTLE
   SET
        AWAY_TEAM = #{teamNo}
 WHERE
        BATTLE_NO = #{battleNo}

mapper.insert:
INSERT
  INTO
        CHATTING
        (
        CHAT_NO
        ,MOIM_NO
        ,MEM_NO
        ,CHAT_CONTENT
        )
VALUES
        (
        SEQ_CNO.NEXTVAL
        ,#{battleNo}
        ,#{memNo}
        ,#{chatContent}
        )
