mapper.insert:
insert 
  into
		board
		(
		board_no
		,title
		,mem_no
		,content
		,category
		,origin_name
		,change_name
		)
 values	
		(
		seq_bno.nextval
		,#{title}
		,#{memberNo}
		,#{content}
		,#{category}
		,#{originName}
		,#{changeName}
		)

mapper.update:
update
		board
set
		title = #{title}		
		,content = #{content}
		,mem_no = #{memberNo}
		,origin_Name = #{originName}
		,change_Name = #{changeName}
where 
		board_no = #{boardNo}
