mapper.delete:
DELETE
	   CERT
 WHERE
 	   CERT_IP = #{certIp}	
   AND 
       SECRET_NO = #{secretNo}

mapper.update:
UPDATE MEMBER
NICKNAME = #{nickName}, PHONE = #{phone}, EMAIL = #{email}, MEM_AREA = #{memArea}, DETAIL_AREA = #{detailArea}, GRADE = #{grade}
WHERE MEM_ID = #{memId} AND MEM_NO = #{memNo}

mapper.select:
SELECT POINT_NO, MEM_NO, POINT_CONTENT, POINT, TO_CHAR(POINT_DATE, 'YYYY-MM-DD') as "POINT_DATE", CATEGORY FROM POINT WHERE MEM_NO = #{memNo}
AND CATEGORY = #{category}
ORDER BY POINT_NO DESC
